CREATE TABLE `tbl_newses` (
  `news_id` int(11) NOT NULL,
  `category_id` int(11) NOT NULL,
  `subcategory_id` int(11) NOT NULL,
  `top_news` tinyint(1) NOT NULL,
  `news_title` varchar(150) NOT NULL,
